mapper.select:
select count(*)
from portfolio
where user_id=#{id}

select count(*)
from recruitment_bookmark
where user_id=#{id}

select count(*)
from portfolio_bookmark
where user_id=#{id}

select count(*)
from portfolio
where portfolio_id in

    #{item}

and user_id != #{id}

select count(*)
from follow
where follower_id = #{userid}
  and following_id = #{id}

mapper.delete:
delete
from follow
where follower_id = #{userid}
  and following_id = #{id}

mapper.select:
select pb.portfolio_id
from portfolio_bookmark pb
         join portfolio p
              on pb.portfolio_id = p.portfolio_id
where pb.user_id = #{userid}
  and p.user_id = #{id}

select pb.RECRUITMENT_ID
from recruitment_bookmark pb
         join recruitment p
              on pb.RECRUITMENT_ID = p.RECRUITMENT_ID
where pb.user_id = #{userid}
  and p.user_id = #{id}
